const uint8_t * tasxxxx_volume_get_data_for_db(int8_t volume_db)
{
    if (volume_db < -90)
    {
        return tasxxxx_volume_table[0];
    }
    else if (volume_db > 20)
    {
        return tasxxxx_volume_table[111];
    }
    else
    {
        return tasxxxx_volume_table[volume_db + 90];
    }
}
